mapper.insert:
INSERT INTO system_notices (
    title, content, publisher_id, publish_time,
    status, read_count
) VALUES (
             #{title}, #{content}, #{publisherId}, #{publishTime},
             #{status}, #{readCount}
         )

mapper.update:
UPDATE system_notices SET
                          title = #{title},
                          content = #{content},
                          publisher_id = #{publisherId}
WHERE notice_id = #{noticeId}

UPDATE system_notices SET
    status = #{status}
WHERE notice_id = #{noticeId}

UPDATE system_notices SET
    read_count = read_count + 1
WHERE notice_id = #{noticeId}

mapper.select:
SELECT * FROM system_notices

    AND status = #{status}

ORDER BY publish_time DESC
LIMIT #{pageNum}, #{pageSize}
