mapper.insert:
INSERT INTO user_collections (
    user_id, collection_type, target_id, collection_time
) VALUES (
             #{userId}, #{collectionType}, #{targetId}, #{collectionTime}
         )

mapper.delete:
DELETE FROM user_collections
WHERE user_id = #{userId}
  AND collection_type = #{collectionType}
  AND target_id = #{targetId}

mapper.select:
SELECT c.*, CASE WHEN c.collection_type = 1 THEN r.route_name WHEN c.collection_type = 2 THEN s.station_name ELSE NULL END AS target_name FROM user_collections c LEFT JOIN bus_routes r ON c.collection_type = 1 AND c.target_id = r.route_id LEFT JOIN bus_stations s ON c.collection_type = 2 AND c.target_id = s.station_id WHERE c.user_id = #{userId}
AND c.collection_type = #{collectionType}
ORDER BY c.collection_time DESC

SELECT * FROM user_collections
WHERE user_id = #{userId}
  AND collection_type = #{collectionType}
  AND target_id = #{targetId}
LIMIT 1

SELECT c.*, CASE WHEN c.collection_type = 1 THEN r.route_name WHEN c.collection_type = 2 THEN s.station_name ELSE NULL END AS target_name FROM user_collections c LEFT JOIN bus_routes r ON c.collection_type = 1 AND c.target_id = r.route_id LEFT JOIN bus_stations s ON c.collection_type = 2 AND c.target_id = s.station_id WHERE c.user_id = #{userId}
AND c.collection_type = #{collectionType}
ORDER BY c.collection_time DESC LIMIT #{pageNum}, #{pageSize}

SELECT COUNT(*) FROM user_collections
WHERE user_id = #{userId}

    AND collection_type = #{collectionType}
