mapper.insert:
INSERT INTO users (
    username, password, role_type, phone, create_time
) VALUES (
    #{username}, #{password}, #{roleType}, #{phone}, #{createTime}
)

mapper.update:
UPDATE users SET
    username = #{username},
    role_type = #{roleType},
    phone = #{phone}
WHERE user_id = #{userId}

UPDATE users SET
    password = #{newPassword}
WHERE user_id = #{userId}
